    public static void heapSort(String[] transactions) {
        int len = transactions.length;
        for (int i = transactions.length - 1; i >= 0; i--) {
            heapify(transactions, i, len);
        }
        for (int i = transactions.length - 1; i > 0; i--) {
            swap(transactions, 0, i);
            heapify(transactions, 0, i);
        }
    }

    public static void swap(String[] transactions, int i, int j) {
        String temp = transactions[i];
        transactions[i] = transactions[j];
        transactions[j] = temp;
    }

    public static void heapify(String[] transactions, int i, int len) {

        int temp = Integer.parseInt(transactions[i].split(",")[1]);
        for (int k = 2 * i + 1; k < len; k = 2 * k + 1) {
            if (k + 1 < len && Integer.parseInt(transactions[k].split(",")[1]) < Integer.parseInt(transactions[k + 1].split(",")[1])) {  //如果有右子树,并且右子树大于左子树
                k++;
            }
            if (Integer.parseInt(transactions[k].split(",")[1]) > temp) {
                swap(transactions, i, k);
                i = k;
            } else {
                break;
            }
        }
    }

